import socket
import math
import sys
import vs

unitsPerMeter = 53.33

def UnitsToMeters(vec):
	return [i/unitsPerMeter for i in vec]
#end

def MetersToUnits(vec):
	return [i*unitsPerMeter for i in vec]
#end

def VectorToString(vec):
	temp = UnitsToMeters([vec.x, vec.y, vec.z])
	return ','.join([str(i) for i in temp])
#end

def StringToVector(data):
	temp = MetersToUnits([float(x) for x in data.split(',')])
	return vs.Vector(temp[0], temp[1], temp[2])
#end

def QuaternionToString(quat):
	temp = [quat.x, quat.y, quat.z, quat.w]
	return ','.join([str(i) for i in temp])
#end

def StringToQuaternion(data):
	temp = [float(x) for x in data.split(',')]
	return vs.Quaternion(temp[0], temp[1], temp[2], temp[3])
#end