import socket
import math
import sys

unitsPerMeter = 53.33

def UnitsToMeters(vec):
	ret = vec;
	for i in range(len(ret)):
		ret[i] /= unitsPerMeter
	
	return ret
#end

def MetersToUnits(vec):
	ret = vec;
	for i in range(len(ret)):
		ret[i] *= unitsPerMeter
	
	return ret
#end

def VecToString(vec):
	return ','.join([str(i) for i in vec])
#end

def StringToVec(data):
	return [float(x) for x in data.split(',')]
#end